import { initializeApp } from "firebase/app";
import { getFirestore } from "firebase/firestore";
import { collection, addDoc } from "firebase/firestore";
const firebaseConfig = {
  apiKey: "AIzaSyDjwLR6R3F2px_BQCDVYewuwk_wal3IAqI",
  authDomain: "new-app-c41e6.firebaseapp.com",
  projectId: "new-app-c41e6",
  storageBucket: "new-app-c41e6.firebasestorage.app",
  messagingSenderId: "382419336824",
  appId: "1:382419336824:web:63e7071f907122d16b778d",
  measurementId: "G-XDM9QN6NFQ"
};


const app = initializeApp(firebaseConfig);
 const db = getFirestore(app);

const saveBtn = document.getElementById("saveBtn");
const nameInput = document.getElementById("nameInput");

saveBtn.addEventListener("click", async () => {
  const name = nameInput.value;  // берём введённое имя

  if (name === "") {
    alert("Введите имя!");
    return;
  }

  try {
    // добавляем имя в коллекцию "users"
    await addDoc(collection(db, "users"), {
      name: name,
      createdAt: new Date()  // когда добавлено
    });
    alert("Имя сохранено!");
    nameInput.value = ""; // очищаем поле
  } catch (e) {
    console.error("Ошибка при сохранении:", e);
  }
});
